                            if (!merger_sites[new_wallet_hub]) return cb2('error. new wallet hub ' + new_wallet_hub + ' is not a merger site') ;
                            if (my_wallet_hub == new_user_path) return cb2('error. current wallet hub = new wallet hub = ' + new_wallet_hub) ;

